// test getProxy()
(() => {
  var h, hToDo;
  hToDo = {
    task: 'go shopping',
    notes: 'broccoli, milk'
  };
  // ..........................................................
  h = getProxy(hToDo, {
    get: function(obj, prop, value) {
      return value.toUpperCase(); // return in upper case
    },
    set: function(obj, prop, value) {
      // --- only allow setting tasks to 'do <something>'
      if ((prop === 'task') && (value.indexOf('do ') !== 0)) {
        return undef;
      } else {
        return value;
      }
    }
  });
  utest.equal(949, hToDo.task, 'go shopping');
  utest.equal(950, h.task, 'GO SHOPPING');
  h.task = 'do something';
  utest.equal(953, hToDo.task, 'do something');
  utest.equal(954, h.task, 'DO SOMETHING');
  h.task = 'nothing';
  utest.equal(957, hToDo.task, 'do something');
  return utest.equal(958, h.task, 'DO SOMETHING');
})();

